import React from 'react';

const ExperienceSection = () => {
  const experienceData = [
    {
      position: "Frontend Developer",
      company: "Tech Solutions Ltd.",
      duration: "Jan 2023 - Present",
      type: "Full-time",
      location: "Remote",
      description: "Developing responsive web applications using React.js and modern frontend technologies. Collaborating with cross-functional teams to deliver high-quality user experiences.",
      responsibilities: [
        "Built 15+ responsive web applications using React.js and Tailwind CSS",
        "Improved website performance by 40% through code optimization",
        "Collaborated with UI/UX designers to implement pixel-perfect designs",
        "Mentored 2 junior developers in modern frontend practices"
      ],
      technologies: ["React.js", "JavaScript", "Tailwind CSS", "Git", "Figma"],
      icon: "work"
    },
    {
      position: "Web Developer Intern",
      company: "Digital Agency Pro",
      duration: "Jun 2022 - Dec 2022",
      type: "Internship",
      location: "On-site",
      description: "Gained hands-on experience in full-stack web development, working on client projects and learning industry best practices.",
      responsibilities: [
        "Developed 5+ client websites using HTML, CSS, and JavaScript",
        "Assisted in WordPress theme customization and plugin development",
        "Participated in code reviews and team meetings",
        "Learned version control with Git and project management tools"
      ],
      technologies: ["HTML", "CSS", "JavaScript", "WordPress", "PHP"],
      icon: "business_center"
    }
  ];

  return (
    <section className="px-6 lg:px-16 py-24 max-w-7xl mx-auto w-full relative z-10 border-t border-slate-200/50 dark:border-slate-800/50" id="experience">
      {/* Section Header */}
      <div className="flex flex-col items-center text-center mb-16">
        <div className="inline-block px-4 py-1.5 rounded-full bg-orange-500/10 border border-orange-500/20 mb-4">
          <span className="text-orange-500 font-semibold text-xs tracking-widest uppercase">My Journey</span>
        </div>
        <h2 className="text-3xl md:text-5xl font-bold text-slate-900 dark:text-white">
          Work <span className="bg-clip-text text-transparent bg-gradient-to-r from-orange-500 to-red-500">Experience</span>
        </h2>
        <div className="w-24 h-1.5 bg-gradient-to-r from-orange-500 to-red-500 rounded-full mt-4"></div>
      </div>

      {/* Experience Timeline */}
      <div className="relative">
        {/* Timeline Line */}
        <div className="absolute left-8 md:left-1/2 md:transform md:-translate-x-1/2 w-1 h-full bg-gradient-to-b from-orange-500 to-red-500 rounded-full"></div>
        
        <div className="space-y-12">
          {experienceData.map((exp, index) => (
            <div key={index} className={`flex flex-col md:flex-row items-start md:items-center gap-8 ${index % 2 === 0 ? 'md:flex-row' : 'md:flex-row-reverse'}`}>
              {/* Timeline Dot */}
              <div className="absolute left-6 md:left-1/2 md:transform md:-translate-x-1/2 w-6 h-6 rounded-full bg-gradient-to-r from-orange-500 to-red-500 border-4 border-white dark:border-background-dark shadow-lg z-10"></div>

              {/* Content Card */}
              <div className="flex-1 ml-16 md:ml-0 group">
                <div className="relative p-8 bg-white/50 dark:bg-surface-dark/50 backdrop-blur-md rounded-3xl border border-slate-200 dark:border-slate-700 shadow-xl hover:shadow-2xl hover:shadow-orange-500/10 transition-all duration-300 hover:-translate-y-2">
                  <div className="absolute top-0 left-0 w-full h-1 bg-gradient-to-r from-transparent via-orange-500 to-transparent opacity-0 group-hover:opacity-100 transition-opacity"></div>
                  
                  {/* Header */}
                  <div className="flex items-start gap-4 mb-6">
                    <div className="w-12 h-12 rounded-xl bg-orange-500/10 flex items-center justify-center text-orange-500 flex-shrink-0">
                      <span className="material-icons-outlined text-2xl">{exp.icon}</span>
                    </div>
                    <div className="flex-1">
                      <h3 className="text-xl font-bold text-slate-900 dark:text-white mb-2 group-hover:text-orange-500 transition-colors">
                        {exp.position}
                      </h3>
                      <div className="flex flex-col sm:flex-row sm:items-center gap-2 mb-3">
                        <span className="text-primary font-semibold">{exp.company}</span>
                        <span className="hidden sm:inline text-slate-400">â€¢</span>
                        <span className="text-slate-600 dark:text-slate-400">{exp.duration}</span>
                      </div>
                      <div className="flex gap-2 mb-4">
                        <span className="inline-block px-3 py-1 rounded-full bg-orange-500/10 text-orange-500 text-xs font-semibold">
                          {exp.type}
                        </span>
                        <span className="inline-block px-3 py-1 rounded-full bg-slate-200 dark:bg-slate-700 text-slate-600 dark:text-slate-300 text-xs font-semibold">
                          {exp.location}
                        </span>
                      </div>
                    </div>
                  </div>
                  
                  {/* Description */}
                  <p className="text-slate-600 dark:text-slate-400 text-sm leading-relaxed mb-6">
                    {exp.description}
                  </p>

                  {/* Responsibilities */}
                  <div className="mb-6">
                    <h4 className="text-slate-900 dark:text-white font-semibold mb-3">Key Responsibilities:</h4>
                    <ul className="space-y-2">
                      {exp.responsibilities.map((responsibility, idx) => (
                        <li key={idx} className="flex items-start gap-2 text-slate-600 dark:text-slate-400 text-sm">
                          <span className="w-1.5 h-1.5 rounded-full bg-orange-500 mt-2 flex-shrink-0"></span>
                          {responsibility}
                        </li>
                      ))}
                    </ul>
                  </div>

                  {/* Technologies */}
                  <div>
                    <h4 className="text-slate-900 dark:text-white font-semibold mb-3">Technologies Used:</h4>
                    <div className="flex flex-wrap gap-2">
                      {exp.technologies.map((tech, idx) => (
                        <span key={idx} className="px-3 py-1 rounded-full bg-slate-100 dark:bg-slate-800 text-slate-700 dark:text-slate-300 text-xs font-medium">
                          {tech}
                        </span>
                      ))}
                    </div>
                  </div>
                </div>
              </div>

              {/* Spacer for alternating layout */}
              <div className="flex-1 hidden md:block"></div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default ExperienceSection;
