import React, { useState } from 'react';

const Header = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const navLinks = [
    { name: 'Home', href: '#', icon: 'home' },
    { name: 'About', href: '#about', icon: 'person' },
    { name: 'Skills', href: '#skills', icon: 'bolt' },
    { name: 'Education', href: '#education', icon: 'school' },
    { name: 'Experience', href: '#experience', icon: 'work_outline' },
    { name: 'Projects', href: '#projects', icon: 'folder_open' },
    { name: 'Contact', href: '#contact', icon: 'mail_outline' }
  ];

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  return (
    <>
      <header className="w-full px-6 py-4 flex justify-between items-center bg-white/50 dark:bg-[#0B1121]/80 backdrop-blur-md sticky top-0 z-40 border-b border-slate-200/50 dark:border-slate-800/50">
        {/* Mobile Logo */}
        <div className="flex items-center gap-2 md:hidden">
          <div className="w-10 h-10 rounded-lg bg-gradient-to-br from-primary to-secondary flex items-center justify-center text-white font-bold">
            AH
          </div>
        </div>

        {/* Desktop Navigation */}
        <nav className="hidden md:flex ml-auto items-center bg-white dark:bg-surface-dark px-2 py-1.5 rounded-full border border-slate-200 dark:border-slate-700 shadow-lg dark:shadow-none">
          {navLinks.map((link, index) => (
            <a 
              key={index}
              className={`flex items-center gap-2 px-4 py-2 rounded-full font-medium transition-colors ${
                index === 0 
                  ? 'bg-primary/10 text-primary' 
                  : 'text-slate-600 dark:text-slate-400 hover:text-primary dark:hover:text-white'
              }`}
              href={link.href}
            >
              <span className="material-icons-outlined text-sm">{link.icon}</span>
              <span className="text-sm">{link.name}</span>
            </a>
          ))}
        </nav>

        {/* Mobile Menu Button */}
        <button 
          className="md:hidden text-slate-800 dark:text-white p-2"
          onClick={toggleMobileMenu}
        >
          <span className="material-icons-outlined">
            {isMobileMenuOpen ? 'close' : 'menu'}
          </span>
        </button>
      </header>

      {/* Mobile Menu */}
      {isMobileMenuOpen && (
        <div className="md:hidden fixed inset-0 z-50 bg-black/50 backdrop-blur-sm">
          <div className="bg-white dark:bg-surface-dark w-80 h-full shadow-xl">
            <div className="p-6">
              <div className="flex items-center justify-between mb-8">
                <div className="flex items-center gap-3">
                  <div className="w-10 h-10 rounded-lg bg-gradient-to-br from-primary to-secondary flex items-center justify-center text-white font-bold">
                    AH
                  </div>
                  <div>
                    <h3 className="font-bold text-slate-900 dark:text-white">MD. Anwar Hossen</h3>
                    <p className="text-sm text-slate-500 dark:text-slate-400">MERN Stack Developer</p>
                  </div>
                </div>
                <button 
                  onClick={toggleMobileMenu}
                  className="w-8 h-8 rounded-full bg-slate-100 dark:bg-slate-800 flex items-center justify-center text-slate-500 dark:text-slate-400"
                >
                  <span className="material-icons-outlined text-sm">close</span>
                </button>
              </div>
              
              <nav className="space-y-2">
                {navLinks.map((link, index) => (
                  <a
                    key={index}
                    href={link.href}
                    onClick={toggleMobileMenu}
                    className="flex items-center gap-3 px-4 py-3 rounded-xl text-slate-700 dark:text-slate-300 hover:bg-primary/10 hover:text-primary transition-colors"
                  >
                    <span className="material-icons-outlined text-xl">{link.icon}</span>
                    <span className="font-medium">{link.name}</span>
                  </a>
                ))}
              </nav>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default Header;
