import React from 'react';

const SkillBar = ({ skill, percentage, icon, color = "primary" }) => {
  const colorClasses = {
    primary: "from-primary to-blue-400",
    secondary: "from-secondary to-pink-500"
  };

  return (
    <div className="group">
      <div className="flex justify-between items-center mb-2">
        <span className="text-slate-700 dark:text-slate-300 font-medium flex items-center gap-2">
          <i className={`${icon} ${skill === 'HTML5' ? 'text-orange-500' : 
                                   skill === 'CSS3' ? 'text-blue-500' : 
                                   skill === 'JavaScript' ? 'text-yellow-400' : 
                                   skill === 'React JS' ? 'text-cyan-400' : 
                                   skill === 'Tailwind CSS' ? 'text-cyan-500' :
                                   skill === 'Node JS' ? 'text-green-500' :
                                   skill === 'Express JS' ? 'text-gray-500' :
                                   skill === 'MongoDB' ? 'text-green-600' :
                                   skill === 'Firebase' ? 'text-yellow-500' :
                                   skill === 'WordPress' ? 'text-blue-700' : ''}`}></i>
          {skill}
        </span>
        <span className={`text-${color} text-sm font-bold`}>{percentage}%</span>
      </div>
      <div className="w-full h-2.5 bg-slate-200 dark:bg-slate-700 rounded-full overflow-hidden">
        <div 
          className={`h-full bg-gradient-to-r ${colorClasses[color]} rounded-full group-hover:scale-x-105 origin-left transition-transform duration-500`}
          style={{ width: `${percentage}%` }}
        ></div>
      </div>
    </div>
  );
};

const ToolCard = ({ icon, name, hoverColor }) => (
  <div className="flex flex-col items-center justify-center gap-3 p-4 rounded-2xl bg-slate-100 dark:bg-slate-800/50 hover:bg-white dark:hover:bg-surface-dark border border-transparent hover:border-primary/30 transition-all duration-300 hover:-translate-y-1 cursor-pointer group">
    <i className={`${icon} text-4xl ${hoverColor} group-hover:scale-110 transition-transform`}></i>
    <span className="text-slate-700 dark:text-slate-300 font-medium">{name}</span>
  </div>
);

const SkillsSection = () => {
  const frontendSkills = [
    { skill: 'HTML5', percentage: 95, icon: 'fab fa-html5' },
    { skill: 'CSS3', percentage: 90, icon: 'fab fa-css3-alt' },
    { skill: 'JavaScript', percentage: 85, icon: 'fab fa-js' },
    { skill: 'React JS', percentage: 90, icon: 'fab fa-react' },
    { skill: 'Tailwind CSS', percentage: 92, icon: 'fab fa-css3' }
  ];

  const backendSkills = [
    { skill: 'Node JS', percentage: 80, icon: 'fab fa-node' },
    { skill: 'Express JS', percentage: 75, icon: 'fas fa-server' },
    { skill: 'MongoDB', percentage: 85, icon: 'fas fa-database' },
    { skill: 'Firebase', percentage: 70, icon: 'fas fa-fire' },
    { skill: 'WordPress', percentage: 85, icon: 'fab fa-wordpress' }
  ];

  const tools = [
    { icon: 'fab fa-git-alt', name: 'Git', hoverColor: 'text-orange-600' },
    { icon: 'fab fa-github', name: 'GitHub', hoverColor: 'text-slate-800 dark:text-white' },
    { icon: 'fas fa-code', name: 'VS Code', hoverColor: 'text-blue-500' },
    { icon: 'fab fa-figma', name: 'Figma', hoverColor: 'text-pink-500' },
    { icon: 'fas fa-paper-plane', name: 'Postman', hoverColor: 'text-orange-500' },
    { icon: 'fab fa-npm', name: 'NPM', hoverColor: 'text-red-600' }
  ];

  return (
    <section 
      className="px-6 lg:px-16 py-24 max-w-7xl mx-auto w-full relative z-10 border-t border-slate-200/50 dark:border-slate-800/50" 
      id="skills"
    >
      {/* Section Header */}
      <div className="flex flex-col items-center text-center mb-16">
        <div className="inline-block px-4 py-1.5 rounded-full bg-secondary/10 border border-secondary/20 mb-4">
          <span className="text-secondary font-semibold text-xs tracking-widest uppercase">My Expertise</span>
        </div>
        <h2 className="text-3xl md:text-5xl font-bold text-slate-900 dark:text-white">
          Technical <span className="bg-clip-text text-transparent bg-gradient-to-r from-secondary to-primary">Skills</span>
        </h2>
        <div className="w-24 h-1.5 bg-gradient-to-r from-secondary to-primary rounded-full mt-4"></div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-12 gap-8">
        {/* Frontend Skills */}
        <div className="lg:col-span-6 p-8 bg-white/50 dark:bg-surface-dark/50 backdrop-blur-md rounded-3xl border border-slate-200 dark:border-slate-700 shadow-xl">
          <div className="flex items-center gap-4 mb-8">
            <div className="w-12 h-12 rounded-xl bg-primary/10 flex items-center justify-center text-primary">
              <span className="material-icons-outlined text-2xl">code</span>
            </div>
            <h3 className="text-2xl font-bold text-slate-900 dark:text-white">Frontend Developer</h3>
          </div>
          <div className="space-y-6">
            {frontendSkills.map((skillData, index) => (
              <SkillBar 
                key={index}
                skill={skillData.skill}
                percentage={skillData.percentage}
                icon={skillData.icon}
                color="primary"
              />
            ))}
          </div>
        </div>

        {/* Backend Skills */}
        <div className="lg:col-span-6 p-8 bg-white/50 dark:bg-surface-dark/50 backdrop-blur-md rounded-3xl border border-slate-200 dark:border-slate-700 shadow-xl">
          <div className="flex items-center gap-4 mb-8">
            <div className="w-12 h-12 rounded-xl bg-secondary/10 flex items-center justify-center text-secondary">
              <span className="material-icons-outlined text-2xl">dns</span>
            </div>
            <h3 className="text-2xl font-bold text-slate-900 dark:text-white">Backend Developer</h3>
          </div>
          <div className="space-y-6">
            {backendSkills.map((skillData, index) => (
              <SkillBar 
                key={index}
                skill={skillData.skill}
                percentage={skillData.percentage}
                icon={skillData.icon}
                color="secondary"
              />
            ))}
          </div>
        </div>

        {/* Tools & Technologies */}
        <div className="lg:col-span-12 p-8 bg-white/50 dark:bg-surface-dark/50 backdrop-blur-md rounded-3xl border border-slate-200 dark:border-slate-700 shadow-xl">
          <div className="flex flex-col md:flex-row items-center justify-between gap-4 mb-8">
            <div className="flex items-center gap-4">
              <div className="w-12 h-12 rounded-xl bg-slate-500/10 flex items-center justify-center text-slate-500 dark:text-slate-300">
                <span className="material-icons-outlined text-2xl">build</span>
              </div>
              <h3 className="text-2xl font-bold text-slate-900 dark:text-white">Tools & Technologies</h3>
            </div>
            <p className="text-slate-500 dark:text-slate-400 text-sm">Tools I use to supercharge my workflow</p>
          </div>
          <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-6">
            {tools.map((tool, index) => (
              <ToolCard 
                key={index}
                icon={tool.icon}
                name={tool.name}
                hoverColor={tool.hoverColor}
              />
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default SkillsSection;
