import React, { useEffect } from 'react';
import Sidebar from './components/Sidebar';
import Header from './components/Header';
import HeroSection from './components/HeroSection';
import AboutSection from './components/AboutSection';
import SkillsSection from './components/SkillsSection';
import EducationSection from './components/EducationSection';
import ExperienceSection from './components/ExperienceSection';
import ProjectsSection from './components/ProjectsSection';
import ContactSection from './components/ContactSection';
import Footer from './components/Footer';

function App() {
  useEffect(() => {
    // Set dark mode by default if system prefers dark mode
    if (window.matchMedia && window.matchMedia('(prefers-color-scheme: dark)').matches) {
      document.documentElement.classList.add('dark');
    }
  }, []);

  return (
    <div className="bg-background-light dark:bg-background-dark text-slate-800 dark:text-slate-200 font-sans min-h-screen relative overflow-x-hidden selection:bg-primary selection:text-white transition-colors duration-300">
      {/* Background Elements */}
      <div className="fixed inset-0 pointer-events-none z-0 overflow-hidden">
        <div className="absolute top-[-10%] left-[-10%] w-[40rem] h-[40rem] bg-primary/10 rounded-full blur-3xl opacity-30 dark:opacity-20"></div>
        <div className="absolute bottom-[-10%] right-[-10%] w-[40rem] h-[40rem] bg-secondary/10 rounded-full blur-3xl opacity-30 dark:opacity-20"></div>
        <div className="absolute top-1/4 left-1/4 text-9xl text-slate-200 dark:text-slate-800 opacity-20 font-mono select-none">&lt;/&gt;</div>
        <div className="absolute bottom-1/3 right-1/4 text-9xl text-slate-200 dark:text-slate-800 opacity-20 font-mono select-none">{ }</div>
      </div>

      <div className="flex flex-col md:flex-row h-screen relative z-10">
        <Sidebar />
        <main className="flex-1 md:ml-24 flex flex-col h-full overflow-y-auto scroll-smooth">
          <Header />
          <HeroSection />
          <AboutSection />
          <SkillsSection />
          <EducationSection />
          <ExperienceSection />
          <ProjectsSection />
          <ContactSection />
        </main>
      </div>
      <Footer />
    </div>
  );
}

export default App;
